import React, { Component } from 'react';
import Countdown from 'react-countdown-now';
import moment from 'moment';
import './App.css';

class App extends Component {

  getToDate = () => {
    const toDate = moment("2018-12-22T00:35").subtract(4, "hours").subtract(30, "minutes") - moment();
    console.log(toDate);
    return toDate;
  }

  render() {
    let toDate = new Date();
    return (
      <div className="App">
        <div className="Title">B+B nedräknare</div>
        <div className="time"></div>
        <Countdown
          date={moment() + this.getToDate()}
          renderer={renderer}
        />
      </div>
    );
  }
}

export default App;

const renderer = ({ days, hours, minutes, seconds, completed }) => {
  if (completed) {
    return <Completionist />;
  } else {
    return <div className="counter">{days} Dagar {hours} Timmar {minutes} minuter {seconds} Sekunder</div>
  }
}

const Completionist = () => <span>Nu händer det!</span>
