class ProgressBarExample extends React.Component {
    constructor(props) {
      super(props)

      this.state = {
        percentage: 0
      }
      this.nextStep = this.nextStep.bind(this)
    }

    nextStep() {
      if(this.state.percentage === 100) return 
      this.setState({ percentage: this.state.percentage + 20 })
    }

    render() {
      return (
        <div>

          <h2> A React Progress Bar </h2>
          <ProgressBar percentage={this.state.percentage} />

          <div style={{ marginTop: '20px' }}>  
            <button 
              onClick={this.nextStep}
             >
              Next Step
            </button>  
          </div>   

          <div style={{marginTop: '10px', color: 'blue', marginBottom: '15px'}} onClick={() => this.setState({ percentage: 0 })}>
            Reset
          </div>

          <a href="https://www.youtube.com/watch?v=GZ4d3HEn9zg" target="_blank" style={{ marginTop: '20px' }}>
            Check out my free React video tutorial  
          </a>
        </div>
      )
    }  
  }

  const ProgressBar = (props) => {
    return (
        <div className="progress-bar">
          <Filler percentage={props.percentage} />
        </div>
      )
  }

  const Filler = (props) => {
    return <div className="filler" style={{ width: `${props.percentage}%` }} />
  }

  ReactDOM.render(
    <ProgressBarExample />,
    document.querySelector('#app')
  )
