import React, { Component } from 'react';
import Countdown from 'react-countdown-now';
import moment from 'moment';
import './App.css';
import Elephant from './circus-elephant.svg';
import ProgressBar from './ProgressBar';

class App extends Component {

  getToDate = () => {
    const toDate = moment("2018-12-22T00:35").subtract(4, "hours").subtract(30, "minutes") - moment();
    return toDate;
  }

  render() {
    return (
      <div className="App">
        <div className="Title">B + B nedräknare</div>
        <div className="time"></div>
        <Countdown
          date={moment() + this.getToDate()}
          renderer={renderer}
        />
        <div className="progress">
          <ProgressBar />
        </div>
      </div>
    );
  }
}

export default App;

const renderer = ({ days, hours, minutes, seconds, completed }) => {
  if (completed) {
    return <Completionist />;
  } else {
    return <div className="counter">
      <div className="counterItem">
        <div className="counterItemNumber">{days}</div>
        <div className="counterItemLabel">Dagar</div>
      </div>
      <div className="counterItem">
        <div className="counterItemNumber">{hours}</div>
        <div className="counterItemLabel">Timmar</div>
      </div>
      <div className="counterItem">
        <div className="counterItemNumber">{minutes}</div>
        <div className="counterItemLabel">Minuter</div>
      </div>
      <div className="counterItem">
        <div className="counterItemNumber">{seconds}</div>
        <div className="counterItemLabel">Sekunder</div>
      </div>
    </div>
  }
}

const Completionist = () => <span>Nu händer det!</span>
